//验证护照是否正确
function checknumber(number) {
var str = number;
//在JavaScript中，正则表达式只能使用"/"开头和结束，不能使用双引号
var Expression = /(P\d{7})|(G\d{8})/;
var objExp = new RegExp(Expression);
if (objExp.test(str) == true) {
return true;
} else {
return false;
}
};
